"use strict";(function(){var dialogTrigger=document.querySelectorAll("[dialog-trigger]");for(var i=0;i<dialogTrigger.length;i++){dialogTrigger[i].onclick=function(){var dialogWindow=this.nextElementSibling;dialogWindow.classList?dialogWindow.classList.add("open"):dialogWindow.className+=" "+"open"}}var closeButton=document.querySelectorAll("[dialog-close]");for(var i1=0;i1<closeButton.length;i1++){closeButton[i1].onclick=function(){var dialogWindow=this.closest(".dialog");dialogWindow.classList?dialogWindow.classList.remove("open"):dialogWindow.className=dialogWindow.className.replace(new RegExp("(^|\\b)"+"open".split(" ").join("|")+"(\\b|$)","gi")," ")}}})();