"use strict";(function(){var input_group=document.querySelectorAll(".form-control");input_group.forEach(function(el){return el.addEventListener("focus",function(){el.parentElement.classList.add("focused")})});var input_group_focused=document.querySelectorAll(".form-control");input_group_focused.forEach(function(el){return el.addEventListener("focusout",function(){el.parentElement.classList.remove("focused")})});var inputs=document.querySelectorAll("input");var textareas=document.querySelectorAll("textarea");for(var i=0;i<inputs.length;i++){if(inputs[i].hasAttribute("value")){inputs[i].parentElement.classList.add("is-filled")}inputs[i].addEventListener("focus",function(e){this.parentElement.classList.add("is-focused")},false);inputs[i].onkeyup=function(e){if(this.value!=""){this.parentElement.classList.add("is-filled")}else{this.parentElement.classList.remove("is-filled")}};inputs[i].addEventListener("focusout",function(e){if(this.value!=""){this.parentElement.classList.add("is-filled")}this.parentElement.classList.remove("is-focused")},false)}for(var i1=0;i1<textareas.length;i1++){if(textareas[i1].hasAttribute("value")){textareas[i1].parentElement.classList.add("is-filled")}textareas[i1].addEventListener("focus",function(e){this.parentElement.classList.add("is-focused")},false);textareas[i1].onkeyup=function(e){if(this.value!=""){this.parentElement.classList.add("is-filled")}else{this.parentElement.classList.remove("is-filled")}};textareas[i1].addEventListener("focusout",function(e){if(this.value!=""){this.parentElement.classList.add("is-filled")}this.parentElement.classList.remove("is-focused")},false)}})();