"use strict";(function(){var _loop=function(i){dropdown_trigger[i].addEventListener("click",function(){this.parentElement.classList.toggle("open");var dropdown_menu=document.querySelectorAll(".menu-item + .dropdown-menu");for(var i=0;i<dropdown_menu.length;i++){var menu_item=dropdown_menu[i].previousElementSibling;menu_item.addEventListener("click",nestedMenuClickHandler)}});var dropdown=dropdown_trigger[i].nextElementSibling;var close=function(){dropdown.parentElement.classList.remove("open")};window.addEventListener("click",function(e){if(dropdown.parentElement.classList.contains("open")){if(!dropdown.contains(e.target)&&!dropdown.previousElementSibling.contains(e.target)){close()}}})};var nestedMenuClickHandler=function nestedMenuClickHandler(){this.nextElementSibling.classList.toggle("open")};var dropdown_trigger=document.querySelectorAll("[menu-trigger]");for(var i=0;i<dropdown_trigger.length;i++)_loop(i)})();